sys_variable_value.value:
function glideInsert(table,arg,values)   // Function to insert a record
{
   var gr = new GlideRecord(table);
   gr.initialize();
   for(var i = 0; i< arg.length; i++)
   {
      var field = arg[i];
      switch (field) {
      case 'name':
        gr.name = values[i];
        break;
      case 'manager':
        gr.manager = values[i];
        break;
      case 'description':
        gr.description = values[i];
        break;
	  case 'group':
        gr.group = values[i];
        break;
	  case 'role':
        gr.role = values[i];
        break;
	  case 'user':
        gr.user = values[i];
        break;
	  case 'email':
        gr.email = values[i];
        break;
	  case 'type':
        gr.type = values[i];
        break;		  	  
     }
   }
   gr.insert();
   return gr;
}
if(current.cat_item == '3feabce9db9093407b9b9ea3db9619ac') // ESM Café Group Setup Request
{
	if(current.variables.request_type == 'Create a group')
	{
		var gr_test = new GlideRecord('sys_user_group');
		gr_test.addQuery('name', current.variables.create_group_name);
		gr_test.query();
		if(!gr_test.next())     // Group does not exist test
		{
			var group_inserted = glideInsert('sys_user_group',['name','manager','description','email','type'],[current.variables.create_group_name,current.variables.create_group_manager,current.variables.create_group_description,current.variables.create_group_email,'1cb8ab9bff500200158bffffffffff62']);  // type = itil
			
			var role_inserted = glideInsert('sys_group_has_role',['group','role'],[group_inserted.sys_id,'282bf1fac6112285017366cb5f867469']);
			
//			var manager = gs.getUser();
//			manager = manager.­getUserByID(­current.variables.create_group_manager);
//			if(!manager.isMemberOf('ea914cae0f577a00637ce44be1050e16'))  // Group Managers;
//			{
//				var manager_inserted = glideInsert('sys_user_grmember',['group','user'],['ea914cae0f577a00637ce44be1050e16',current.variables.create_group_manager]);
	//		}
			var manager = current.variables.create_group_manager;
			var added_members = "";
			var mem = current.variables.create_group_members;
			var group_members = mem.toString().split(',');
			for(var item in group_members)
			{
				var member_inserted = glideInsert('sys_user_grmember',['group','user'],[group_inserted.sys_id,group_members[item]]);
				added_members = added_members + member_inserted.user.name + ", ";				
			}
			if(current.variables.create_manager_as_member == 'Yes' && !manager.isMemberOf(group_inserted.sys_id))
			{
				gs.addInfoMessage("insidemanager");
				gs.log("manager as member");
				var manager_member_inserted = glideInsert('sys_user_grmember',['group','user'],[group_inserted.sys_id,current.variables.create_group_manager]);
				added_members = added_members + manager_member_inserted.user.name;				
			}
			
			current.comments = group_inserted.name  + " group is created and following members : " + added_members + " has been added.";
		}
		else{
			current.comments =  current.variables.create_group_name + " is an existing group!";
		} 
	}

else if(current.variables.request_type == 'Modify group configuration')
	{  
		if(current.variables.modify_options == 'Group Name')
		{     
			//gs.log("bbbbbbbbb");
			  var old_name = "none";
              if(current.variables.existing_group_name != "")			
			  	old_name = current.variables.existing_group_name.name;
			  var gr_name = new GlideRecord('sys_user_group');
		      gr_name.addQuery('sys_id', current.variables.existing_group_name);
			  gr_name.query();
		      if (gr_name.next()) {
				gr_name.name = current.variables.new_group_name; 
  			  gr_name.update();
				current.comments =  old_name + " group has been renamed as :" + gr_name.name;
   		   }
				
		}
		else if(current.variables.modify_options == 'Group Email')
		{     
			  var old_email = "none";
			  if(current.variables.existing_group_email != "")
			  	old_email = current.variables.existing_group_email;
			  var gr_email = new GlideRecord('sys_user_group');
		      gr_email.addQuery('sys_id', current.variables.group_name);
			  gr_email.query();
		      if (gr_email.next()) {
				gr_email.email = current.variables.new_group_email;
  			  gr_email.update();
				current.comments = "Group email of " + gr_email.name + " group has been changed from : " + old_email + " to : "+ gr_email.email;
   		   }
			gs.addInfoMessage("Email address has been changed");
		}
		else if(current.variables.modify_options == 'Group Manager')
		{
			  var old_manager = "none";
			  if(current.variables.existing_group_manager != "")
			  	old_manager = current.variables.existing_group_manager.name;
			  var gr_manager = new GlideRecord('sys_user_group');
			  gr_manager.addQuery('sys_id',current.variables.group_name);
			  gr_manager.query();
			  if (gr_manager.next()) {
				gr_manager.manager = current.variables.new_group_manager;
  			  gr_manager.update();
				  current.comments = "Group manager of " + gr_manager.name + " group has been changed from : " + old_manager + " to : "+ gr_manager.manager.name;
   		   }
		}
		else if(current.variables.modify_options == 'Add Members')
		{
			var group = current.variables.group_name;
			var add_members = "";
			var mem = current.variables.members_addition;
			var members = mem.toString().split(',');
			for(var item in members)
			{
				var member_added = glideInsert('sys_user_grmember',['group','user'],[group,members[item]]);
				add_members = add_members + member_added.user.name + ", ";				
			}
			current.comments = "following members : " + add_members + " has been added to" + member_added.name;
		}
		else if(current.variables.modify_options == 'Remove Members')
		{
			var members = [];
			var group = current.variables.group_name;
			var remove_members = "";
			var remove_mem = current.variables.members_removal;
			var members = remove_mem.toString().split(',');
			
			var gr_removalGroup = new GlideRecord('sys_user_group');
			gr_removalGroup.addQuery('sys_id',group);
			gr_removalGroup.query();
			if(gr_removalGroup.next()){
				var grMember = new GlideRecord('sys_user_grmember');
				grMember.addQuery('group',group);
				grMember.query();
				while(grMember.next()){
					for(var item in members){
						if(grMember.user == members[item]){
							grMember.deleteRecord();
							break;
						}
					}
				}
			}
		}
	}
else if(current.variables.request_type == 'Deactivate a group')
	{
			  var gr_deactivate = new GlideRecord('sys_user_group');
		      gr_deactivate.addQuery('sys_id', current.variables.deactivate_group_name);
			  gr_deactivate.query();
		      if (gr_deactivate.next()) {
				  var grMember = new GlideRecord('sys_user_grmember');
				  grMember.addQuery('group',gr_deactivate.sys_id);
				  grMember.deleteMultiple();
				  var grAsgnmntLookup = new GlideRecord('dl_u_assignment');
				  grAsgnmntLookup.addQuery('assignment_group',gr_deactivate.sys_id);
				  grAsgnmntLookup.query();
				  while(grAsgnmntLookup.next())
					  {
						  grAsgnmntLookup.active = false;
						  grAsgnmntLookup.update();
					  }  
				gr_deactivate.active = 'false';
  			  gr_deactivate.update();
				current.comments =  gr_deactivate.name + " group has been de-activated." ;
   		   }
	
}
}

